    public static int longestValidParentheses(String s) {
        if (s == null || s.length() == 0)
            return 0;
        int[] dp = new int[s.length()];
        dp[0] = 0;

        ArrayDeque<Character> stack = new ArrayDeque<>();
        //time is exceed might because I using 2 stack and 1 array here, but do we really need these data structure?
        ArrayDeque<Integer> indexStack = new ArrayDeque<>();
        stack.push(s.charAt(0));
        indexStack.push(0);

        int len = 0;
        for (int i = 1; i < s.length(); i++) {
            char curr = s.charAt(i);
            if (curr == ')' && !stack.isEmpty() && stack.peek() == '(') {
                stack.pop();
                int idx = indexStack.pop();
                int templen = i - idx + 1;
                if (idx - 1 >= 0 && (indexStack.isEmpty() || indexStack.peek() != idx - 1))
                    templen += dp[idx - 1];
                len = Math.max(templen, len);
                dp[i] = templen;
            } else {
                stack.push(s.charAt(i));
                indexStack.push(i);
                dp[i] = dp[i - 1];
            }
        }
        return len;
    }

    //TODO:
    public static int longestValidParenthesesFaster(String s) {
        if (s == null || s.length() == 0)
            return 0;

        //only tracking the index, as we can easily get the char by using s.charAt(index)
        ArrayDeque<Integer> indexStack = new ArrayDeque<>(); //only store the index of (
        int maxLen = 0; //result length
        int accumulatedLen = 0; //the length of consecutive valid parentheses

        for (int i = 0; i < s.length(); i++) {
            if (s.charAt(i) == '(') {
                indexStack.push(i);
            } else {//s.charAt(i) is )
                if (indexStack.isEmpty()) {
                    accumulatedLen = 0; //all the char is matched ->()) or there are all ) previously )))
                } else { //char in stack
                    int matchedPos = indexStack.pop();
                    int matchedLen = i - matchedPos + 1; //the length of consecutive parentheses contains matchedPos and i

                    //there are two way to generate current largest length
                    //1: if indexStack is empty, meanings everything is matched, and previous length and current length is connected ,eg ()(())
                    //2. if indexStack is not empty, meanings everything between indexStack.peek() and i is matched, eg(()(())
                    if (indexStack.isEmpty()) {
                        accumulatedLen += matchedLen;
                        matchedLen = accumulatedLen;
                    } else {
                        matchedLen = i - indexStack.peek();
                    }

                    maxLen = Math.max(maxLen, matchedLen);
                }
            }
        }
        return maxLen;
    }

    public static int longestValidParenthesesDP(String s) {
        if (s == null || s.length() == 0)
            return 0;

        int[] dp = new int[s.length()]; //dp is store the micro longest range
        dp[0] = 0;
        int len = 0;
        for (int i = 1; i < s.length(); i++) {
            if (s.charAt(i) == ')') {
                int stackTop = i - dp[i - 1] - 1; //i-dp[i] will definite larger than 0, as dp[i] is the length
                if (stackTop >= 0 && s.charAt(stackTop) == '(') { //in case (|()()()|) when i=7 and dp[7]=6 we just need to check if 7-6-1=0 s.charAt(0) is (, and that means is matched, adding 2 to current dp
                    dp[i] += 2 + dp[i - 1]; //2 is coutn for (stackTop,i) and dp[i-1] is count for the range length between stackTop and i
                    if (stackTop - 1 >= 0) //this step is using to combine the current range length with previous range length if they are connected
                        dp[i] += dp[stackTop - 1];
                } else //means the stacktop can not match dp[i] reset to 0
                    dp[i] = 0;

                len = Math.max(len, dp[i]);
            } else {//when s.charAt(i)=='(',we need to reset the dp value, as dp is the micro length, not the marco max
                dp[i] = 0;
            }
        }
        return len;
    }

